#include <assert.h>
#include <stdio.h>

int hv_necklace(char *s1, char *s2);

char str[]="";

void assert_results(char *s1, char *s2, int expected) {
	int result;

	result = hv_necklace(s1, s2);
	printf("test: hv_necklace(\"%s\", \"%s\")\nexpected_output: %d\nuser_output: %d\n", s1, s2, expected, result);
	fflush(stdout);
	assert(result == expected);
}

int main(void) {
    assert_results("nicole", "icolen", 1);
    assert_results("", "", 1);
    assert_results("aaa", "aaa", 1);
    assert_results("ror", "ror", 1);
    assert_results("rabcr", "abcrr", 1);
    assert_results("rabcr", "bcrra", 1);
    assert_results("jordanegengo", "gengojordane", 1);
    assert_results("titus.paula.disky.", "disky.titus.paula.", 1);
    assert_results("a", "b", 0);
    assert_results("", "icolen", 0);
    assert_results("cdef", "ghij", 0);
    assert_results("AAA", "aaa", 0);
    assert_results("ABC", "CBA", 0);
	assert_results(str, str, 1);
    return (0);
}
